#include <stdio.h>
void main (){
	int n,i,sum;
	printf("input value = ");
	scanf("%d",&i);
	for (i;i>0;i-=1){
		printf(" %d ",i);
		sum+=i;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          i;
	}
	printf("\n%d",sum);
}
